planet_osm_line


(select ST_StartPoint(way) as way from planet_osm_line where "piste:type" LIKE '%nordic%'
) as nodes

&datasource-settings;

(select * from planet_osm_line where 
"piste:type" LIKE '%downhill%'

) as pistes

&datasource-settings;

(select * from planet_osm_line where 
"piste:type" LIKE '%downhill%'

) as pistes

&datasource-settings;

(select * from planet_osm_line where 
"piste:type" LIKE '%downhill%'

) as pistes

&datasource-settings;

(select * from planet_osm_line where 
"piste:type" LIKE '%downhill%'

) as pistes

		&datasource-settings;

		(select * from planet_osm_polygon where 
		"piste:type" LIKE '%downhill%'
        and ("piste:difficulty" is not null)

		) as pistes

		&datasource-settings;

		(select * from planet_osm_polygon where 
		"piste:type" LIKE '%downhill%'
        and ("piste:difficulty" is not null)

		and (ST_X(ST_centroid(way)) > -4000000 or ST_y(ST_centroid(way)) < 0)
		) as pistes

&datasource-settings;

(select * from planet_osm_line where 
"piste:type" LIKE '%downhill%'
and (ST_X(ST_centroid(way)) < -4000000 and ST_y(ST_centroid(way)) > 0)
) as pistes

&datasource-settings;

(select * from planet_osm_polygon where 
"piste:type" LIKE '%downhill%'
and (ST_X(ST_centroid(way)) < -4000000 and ST_y(ST_centroid(way)) > 0)
) as pistes

&datasource-settings;
planet_osm_line

(select * from planet_osm_line where "piste:type" LIKE '%nordic%' and osm_id > 0  order by z_order) as pistes

&datasource-settings;
planet_osm_line

(select * from planet_osm_line where "piste:type" LIKE '%nordic%' and osm_id < 0 order by z_order) as routes

		&datasource-settings;

		(select * from planet_osm_polygon
			where "piste:type" LIKE '%ski_jump%'
            or "piste:type" LIKE '%ski_jump_landing%'
		 ) as pistes

		&datasource-settings;

		(select * from planet_osm_polygon 
			where "piste:type" LIKE '%playground%'
            or "piste:type" LIKE '%snow_park%'
			order by z_order
		 ) as pistes

&datasource-settings;

(select * from planet_osm_point 
	where "man_made" in ('snow_cannon')
	order by z_order
 ) as pistes

&datasource-settings;
planet_osm_line

(select * from planet_osm_line where "piste:type" LIKE '%nordic%' and osm_id < 0 order by z_order) as text

&datasource-settings;
planet_osm_point

(select way, takeoff
from planet_osm_point
	where takeoff is not null order by z_order
  ) as pistes
