        difficulty

            &datasource-settings;

            (SELECT * FROM planet_osm_line where "piste:type"='nordic' and osm_id > 0 order by z_order) as pistes





        no-difficulty

            &datasource-settings;

            (SELECT * FROM planet_osm_line where "piste:type"='nordic' and ("piste:difficulty" not in ('novice','easy','intermediate','advanced','expert','freeride','extreme') or "piste:difficulty" is null) and osm_id > 0 order by z_order) as pistes





        others

            &datasource-settings;

            (select * from planet_osm_line 
                where "piste:type" in ('ice_skate','playground','sled','ski_jump','snow_park','sleigh')
                or sport in ('skating','ski_jump','ski_jump_take_off') order by z_order
             ) as pistes





        others-area

            &datasource-settings;

            (select * from planet_osm_polygon 
                where "piste:type" in ('playground','snow_park')
                order by z_order
             ) as pistes





        aerialways

            &datasource-settings;

            (select * from planet_osm_line where "aerialway" is not null or "railway"='funicular' order by z_order) as pistes





        others-icons

            &datasource-settings;

            (select * from planet_osm_line 
                where "piste:type" in ('ice_skate','playground','sled','ski_jump','snow_park','sleigh')
                or sport in ('skating','ski_jump','ski_jump_take_off') order by z_order
             ) as pistes




        others-icons

            &datasource-settings;

            (select * from planet_osm_polygon 
                where "piste:type" in ('ice_skate','playground','sled','ski_jump','snow_park','downhill')
                or sport in ('skating','ski_jump','ski_jump_take_off') order by z_order
             ) as pistes





        aerialways-icons

            &datasource-settings;
            planet_osm_line

            (select way as way, aerialway, railway 
            from planet_osm_line 
            where ("aerialway" is not null or "railway"='funicular')
            and degrees(st_azimuth(st_startpoint(way), st_endpoint(way))) < 180
            order by z_order) as pistes




        aerialways-icons-rev

            &datasource-settings;
            planet_osm_line

            (select st_reverse(way) as way, aerialway, railway 
            from planet_osm_line 
            where ("aerialway" is not null or "railway"='funicular')
            and degrees(st_azimuth(st_startpoint(way), st_endpoint(way))) > 180
            order by z_order) as pistes





        difficulty-icons

            &datasource-settings;

            (select * from planet_osm_line where "piste:type"='nordic' and osm_id > 0 order by z_order) as pistes




        downhill-icons

            &datasource-settings;

            (select * from planet_osm_line where "piste:difficulty"='freeride' or"piste:grooming"='mogul' or "gladed"='yes' or "lit"='yes' or "piste:lit"='yes' order by z_order) as pistes




        text

            &datasource-settings;
            planet_osm_line

            (select * from planet_osm_line where "piste:type"='nordic' and osm_id < 0 order by z_order) as text




        downhill-text

            &datasource-settings;

            (select way, coalesce("piste:name",name) as name, gladed,lit,"piste:difficulty","piste:grooming","piste:lit" 
            from planet_osm_line 
            where "piste:type"='downhill'
            and (ST_X(ST_centroid(way)) > -4000000 or ST_y(ST_centroid(way)) < 0) 
            order by z_order) as pistes




        downhill-text

            &datasource-settings;

            (select way, coalesce("piste:name",name) as name, gladed,lit,"piste:difficulty","piste:grooming","piste:lit" 
            from planet_osm_polygon 
            where "piste:type"='downhill' and "area"='yes' 
            and (ST_X(ST_centroid(way)) > -4000000 or ST_y(ST_centroid(way)) < 0)
            ) as pistes




